import React, { useState } from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { useInView } from 'react-intersection-observer';

// Plum color palette
const colors = {
  cream: '#FFF8F0',
  plum: '#6C2676',
  coral: '#FF5A5F',
  accent: '#FDE68A',
  dark: '#2D1A2D',
  text: '#3B1F47',
  card: '#F9F6F2',
};

const Bg = styled.div`
  background: ${colors.cream};
  min-height: 100vh;
`;

const Container = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 20px;
`;

const Hero = styled.section`
  min-height: 70vh;
  display: flex;
  align-items: center;
  justify-content: center;
  text-align: center;
  background: ${colors.cream};
`;

const Title = styled(motion.h1)`
  font-size: 3.2rem;
  font-weight: 900;
  color: ${colors.plum};
  margin-bottom: 1rem;
  line-height: 1.1;
  letter-spacing: -2px;
`;

const Subtitle = styled(motion.p)`
  font-size: 1.4rem;
  color: ${colors.text};
  max-width: 600px;
  margin: 0 auto 2rem auto;
`;

const CampaignSection = styled.section`
  padding: 60px 0 30px 0;
  background: ${colors.cream};
`;

const CardRow = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 32px;
  justify-content: center;
`;

const CampaignCard = styled(motion.div)`
  background: ${colors.card};
  border-radius: 24px;
  padding: 36px 32px;
  margin: 12px 0;
  box-shadow: 0 6px 24px rgba(108,38,118,0.08);
  min-width: 320px;
  max-width: 400px;
  flex: 1 1 320px;
  border: 2px solid ${colors.accent};
  transition: transform 0.3s;
  &:hover {
    transform: translateY(-8px) scale(1.03);
    box-shadow: 0 12px 32px rgba(108,38,118,0.13);
  }
`;

const CampaignTitle = styled.h2`
  color: ${colors.coral};
  font-size: 1.5rem;
  margin-bottom: 0.7rem;
  font-weight: 800;
`;

const CampaignDescription = styled.p`
  color: ${colors.text};
  font-size: 1.08rem;
  line-height: 1.6;
`;

const Button = styled(motion.button)`
  background: ${colors.coral};
  color: white;
  border: none;
  padding: 15px 32px;
  border-radius: 30px;
  font-size: 1.1rem;
  cursor: pointer;
  margin-top: 24px;
  font-weight: 700;
  box-shadow: 0 2px 8px rgba(255,90,95,0.08);
  transition: background 0.3s;
  &:hover {
    background: ${colors.plum};
  }
`;

const SectionHeading = styled.h3`
  color: ${colors.plum};
  font-size: 2rem;
  font-weight: 900;
  margin: 2.5rem 0 1.2rem 0;
  text-align: center;
`;

const TestimonialCard = styled.div`
  background: ${colors.plum};
  color: white;
  border-radius: 20px;
  padding: 32px 28px;
  margin: 32px auto 0 auto;
  max-width: 500px;
  box-shadow: 0 6px 24px rgba(108,38,118,0.13);
  font-size: 1.1rem;
  text-align: center;
  position: relative;
`;

const CampaignIdeasList = styled.ul`
  list-style: none;
  padding: 0;
`;

const CampaignIdea = styled.li`
  background: ${colors.card};
  border-left: 6px solid ${colors.coral};
  margin: 18px 0;
  padding: 18px 22px;
  border-radius: 12px;
  font-size: 1.08rem;
  color: ${colors.text};
  box-shadow: 0 2px 8px rgba(255,90,95,0.06);
`;

// --- Campaign Matchmaker Quiz ---
const QuizSection = styled.section`
  background: ${colors.accent};
  padding: 60px 0 40px 0;
  border-radius: 32px;
  margin: 40px 0;
`;
const QuizContainer = styled.div`
  max-width: 500px;
  margin: 0 auto;
  background: ${colors.card};
  border-radius: 24px;
  box-shadow: 0 4px 16px rgba(108,38,118,0.09);
  padding: 36px 28px;
  text-align: center;
`;
const QuizQ = styled.h4`
  color: ${colors.plum};
  font-size: 1.2rem;
  margin-bottom: 1.2rem;
`;
const QuizOptions = styled.div`
  display: flex;
  flex-direction: column;
  gap: 16px;
`;
const QuizOption = styled(Button)`
  background: ${colors.coral};
  color: white;
  font-size: 1rem;
  margin: 0;
  border-radius: 18px;
  padding: 12px 0;
  box-shadow: none;
`;
const QuizResult = styled.div`
  margin-top: 32px;
  background: ${colors.cream};
  border-radius: 18px;
  padding: 24px 18px;
  color: ${colors.text};
  font-size: 1.1rem;
  box-shadow: 0 2px 8px rgba(255,90,95,0.06);
`;

const campaignIdeas = [
  {
    name: 'Plum Pals',
    desc: 'A series of animated shorts featuring quirky, lovable characters explaining insurance in the most human way possible.'
  },
  {
    name: 'Care, Not Claims',
    desc: 'A campaign flipping the scriptâ€”real stories of Plum helping people, not just processing paperwork.'
  },
  {
    name: "Plum's Day Out",
    desc: 'Pop-up "Plum Care Corners" at offices, with wellness games, instant checkups, and surprise treats.'
  },
  {
    name: 'AI, But Make It Empathetic',
    desc: 'Launch an AI-powered "Plum Buddy" that answers insurance questions with a smile (and maybe a meme).'
  },
  {
    name: 'Open Claims, Open Hearts',
    desc: 'Radical transparency campaignâ€”live dashboard of claims processed, with thank-you notes from real users.'
  }
];

const quizQuestions = [
  {
    q: 'What excites you most about a campaign?',
    options: [
      'Fun, quirky storytelling',
      'Real impact on people',
      'Bringing people together',
      'Cool tech & AI',
      'Radical transparency'
    ]
  },
  {
    q: 'Pick a Plum value:',
    options: [
      'Human-first',
      'Empathy',
      'Community',
      'Innovation',
      'Openness'
    ]
  },
  {
    q: 'Your campaign superpower would beâ€¦',
    options: [
      'Turning boring into brilliant',
      'Making people feel seen',
      'Building hype and community',
      'Automating the magic',
      'Telling the truth, beautifully'
    ]
  },
  {
    q: "Just for fun: What's your sun sign?",
    options: [
      'Aries/Leo/Sagittarius',
      'Taurus/Virgo/Capricorn',
      'Gemini/Libra/Aquarius',
      'Cancer/Scorpio/Pisces',
      "I don't believe in sun signs!"
    ]
  }
];

function getMatchedCampaign(answers) {
  // Only use the first 3 answers for matching
  if (answers.length < 4) return null;
  const counts = [0, 0, 0, 0, 0];
  answers.slice(0, 3).forEach(idx => { if (typeof idx === 'number') counts[idx]++; });
  const max = Math.max(...counts);
  const matchIdx = counts.indexOf(max);
  return campaignIdeas[matchIdx];
}

function CampaignMatchmaker() {
  const [step, setStep] = useState(0);
  const [answers, setAnswers] = useState([]);
  const matched = getMatchedCampaign(answers);

  return (
    <QuizSection>
      <QuizContainer>
        <SectionHeading style={{ margin: 0 }}>ðŸŽ¯ Campaign Matchmaker</SectionHeading>
        {step < quizQuestions.length ? (
          <>
            <QuizQ>{quizQuestions[step].q}</QuizQ>
            <QuizOptions>
              {quizQuestions[step].options.map((opt, i) => (
                <QuizOption
                  key={opt}
                  whileHover={{ scale: 1.04 }}
                  whileTap={{ scale: 0.97 }}
                  onClick={() => {
                    setAnswers([...answers, i]);
                    setStep(step + 1);
                  }}
                >
                  {opt}
                </QuizOption>
              ))}
            </QuizOptions>
          </>
        ) : (
          <QuizResult>
            <b>Your Plum-perfect campaign match:</b><br /><br />
            <span style={{ color: colors.plum, fontWeight: 700, fontSize: '1.2rem' }}>{matched.name}</span>
            <br /><br />
            {matched.desc}
            <br /><br />
            <Button
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              onClick={() => {
                setStep(0);
                setAnswers([]);
              }}
              style={{ marginTop: 12 }}
            >
              Try Again
            </Button>
          </QuizResult>
        )}
      </QuizContainer>
    </QuizSection>
  );
}

function App() {
  const [ref, inView] = useInView({ threshold: 0.1, triggerOnce: true });

  return (
    <Bg>
      <Hero>
        <Container>
          <Title
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8 }}
          >
            Meet the Campaign<br />
            <span style={{ color: colors.coral }}>That Campaigns for Campaigns</span>
          </Title>
          <Subtitle
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 0.2 }}
          >
            Not just a marketer. A campaign in human form. <br />
            <b>Anirudh Panda</b> â€” ready to make Plum the most loved name in insurance (with a lot more heart).
          </Subtitle>
          <Button
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            style={{ marginBottom: 32 }}
          >
            Hire the Campaigner-in-Chief
          </Button>
        </Container>
      </Hero>

      <CampaignSection ref={ref}>
        <SectionHeading>Why Anirudh?</SectionHeading>
        <CardRow>
          <CampaignCard
            initial={{ opacity: 0, y: 20 }}
            animate={inView ? { opacity: 1, y: 0 } : {}}
            transition={{ duration: 0.8 }}
          >
            <CampaignTitle>Wit, Warmth, and Wow</CampaignTitle>
            <CampaignDescription>
              I believe in marketing that makes you smile, think, and feel cared for. (And maybe hungry for more.)
            </CampaignDescription>
          </CampaignCard>

          <CampaignCard
            initial={{ opacity: 0, y: 20 }}
            animate={inView ? { opacity: 1, y: 0 } : {}}
            transition={{ duration: 0.8, delay: 0.2 }}
          >
            <CampaignTitle>Heart + Tech = Magic</CampaignTitle>
            <CampaignDescription>
              I blend human-first storytelling with AI and digital wizardry. Because the best campaigns feel like care, not code.
            </CampaignDescription>
          </CampaignCard>

          <CampaignCard
            initial={{ opacity: 0, y: 20 }}
            animate={inView ? { opacity: 1, y: 0 } : {}}
            transition={{ duration: 0.8, delay: 0.4 }}
          >
            <CampaignTitle>Strategy, Not Just Sass</CampaignTitle>
            <CampaignDescription>
              Creative is great. Creative that converts? Even better. I bring ideas that win hearts *and* business.
            </CampaignDescription>
          </CampaignCard>
        </CardRow>
      </CampaignSection>

      <CampaignMatchmaker />

      <CampaignSection>
        <SectionHeading>If Anirudh Ran Campaigns at Plumâ€¦</SectionHeading>
        <CampaignIdeasList>
          <CampaignIdea>
            <b>Plum Pals:</b> A series of animated shorts featuring quirky, lovable characters explaining insurance in the most human way possible.
          </CampaignIdea>
          <CampaignIdea>
            <b>Care, Not Claims:</b> A campaign flipping the scriptâ€”real stories of Plum helping people, not just processing paperwork.
          </CampaignIdea>
          <CampaignIdea>
            <b>Plum's Day Out:</b> Pop-up "Plum Care Corners" at offices, with wellness games, instant checkups, and surprise treats.
          </CampaignIdea>
          <CampaignIdea>
            <b>AI, But Make It Empathetic:</b> Launch an AI-powered "Plum Buddy" that answers insurance questions with a smile (and maybe a meme).
          </CampaignIdea>
          <CampaignIdea>
            <b>Open Claims, Open Hearts:</b> Radical transparency campaignâ€”live dashboard of claims processed, with thank-you notes from real users.
          </CampaignIdea>
        </CampaignIdeasList>
      </CampaignSection>

      <SectionHeading>What Future Plum Teammates Might Sayâ€¦</SectionHeading>
      <TestimonialCard>
        "Anirudh didn't just join the teamâ€”he launched a campaign for us, with us, and about us. Our Slack's never been wittier, our campaigns never more human. 11/10, would campaign again."<br /><br />
        <span style={{ fontWeight: 700, color: colors.accent }}>â€” The Future Plum Team</span>
      </TestimonialCard>

      <CampaignSection>
        <SectionHeading>Ready to Campaign for Plum?</SectionHeading>
        <Container style={{ textAlign: 'center' }}>
          <Button
            whileHover={{ scale: 1.07 }}
            whileTap={{ scale: 0.95 }}
            style={{ fontSize: '1.2rem', marginTop: 16 }}
          >
            Let's Make Insurance Human (and Fun)
          </Button>
        </Container>
      </CampaignSection>
    </Bg>
  );
}

export default App;
