import React, { useState } from 'react';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { useInView } from 'react-intersection-observer';
import { Player } from 'lottie-react';
import heroLottie from './heroLottie.json'; // Place your Lottie JSON file in src/

// Plum color palette
const colors = {
  cream: '#FFF8F0',
  plum: '#6C2676',
  coral: '#FF5A5F',
  accent: '#FDE68A',
  dark: '#2D1A2D',
  text: '#3B1F47',
  card: '#F9F6F2',
};

const Bg = styled.div`
  background: ${colors.cream};
  min-height: 100vh;
`;

const Container = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 20px;
`;

const Hero = styled.section`
  min-height: 70vh;
  display: flex;
  align-items: center;
  justify-content: center;
  text-align: center;
  background: ${colors.cream};
`;

const HeroContent = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  gap: 48px;
  flex-wrap: wrap;
`;

const HeroLottieBox = styled.div`
  width: 260px;
  min-width: 180px;
  @media (max-width: 600px) {
    width: 160px;
  }
`;

const Title = styled(motion.h1)`
  font-size: 3.2rem;
  font-weight: 900;
  color: ${colors.plum};
  margin-bottom: 1rem;
  line-height: 1.1;
  letter-spacing: -2px;
`;

const Subtitle = styled(motion.p)`
  font-size: 1.4rem;
  color: ${colors.text};
  max-width: 600px;
  margin: 0 auto 2rem auto;
`;

const CampaignSection = styled.section`
  padding: 60px 0 30px 0;
  background: ${colors.cream};
`;

const CardRow = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 32px;
  justify-content: center;
`;

const CampaignCard = styled(motion.div)`
  background: ${colors.card};
  border-radius: 24px;
  padding: 36px 32px;
  margin: 12px 0;
  box-shadow: 0 6px 24px rgba(108,38,118,0.08);
  min-width: 320px;
  max-width: 400px;
  flex: 1 1 320px;
  border: 2px solid ${colors.accent};
  transition: transform 0.3s;
  &:hover {
    transform: translateY(-8px) scale(1.03);
    box-shadow: 0 12px 32px rgba(108,38,118,0.13);
  }
`;

const CampaignTitle = styled.h2`
  color: ${colors.coral};
  font-size: 1.5rem;
  margin-bottom: 0.7rem;
  font-weight: 800;
`;

const CampaignDescription = styled.p`
  color: ${colors.text};
  font-size: 1.08rem;
  line-height: 1.6;
`;

const Button = styled(motion.button)`
  background: ${colors.coral};
  color: white;
  border: none;
  padding: 15px 32px;
  border-radius: 30px;
  font-size: 1.1rem;
  cursor: pointer;
  margin-top: 24px;
  font-weight: 700;
  box-shadow: 0 2px 8px rgba(255,90,95,0.08);
  transition: background 0.3s;
  &:hover {
    background: ${colors.plum};
  }
`;

const SectionHeading = styled.h3`
  color: ${colors.plum};
  font-size: 2rem;
  font-weight: 900;
  margin: 2.5rem 0 1.2rem 0;
  text-align: center;
`;

const TestimonialCard = styled.div`
  background: ${colors.plum};
  color: white;
  border-radius: 20px;
  padding: 32px 28px;
  margin: 32px auto 0 auto;
  max-width: 500px;
  box-shadow: 0 6px 24px rgba(108,38,118,0.13);
  font-size: 1.1rem;
  text-align: center;
  position: relative;
`;

const CampaignIdeasList = styled.ul`
  list-style: none;
  padding: 0;
`;

const CampaignIdea = styled.li`
  background: ${colors.card};
  border-left: 6px solid ${colors.coral};
  margin: 18px 0;
  padding: 18px 22px;
  border-radius: 12px;
  font-size: 1.08rem;
  color: ${colors.text};
  box-shadow: 0 2px 8px rgba(255,90,95,0.06);
`;

// --- Campaign Matchmaker Quiz ---
const QuizSection = styled.section`
  background: ${colors.accent};
  padding: 60px 0 40px 0;
  border-radius: 32px;
  margin: 40px 0;
`;
const QuizContainer = styled.div`
  max-width: 500px;
  margin: 0 auto;
  background: ${colors.card};
  border-radius: 24px;
  box-shadow: 0 4px 16px rgba(108,38,118,0.09);
  padding: 36px 28px;
  text-align: center;
`;
const QuizQ = styled.h4`
  color: ${colors.plum};
  font-size: 1.2rem;
  margin-bottom: 1.2rem;
`;
const QuizOptions = styled.div`
  display: flex;
  flex-direction: column;
  gap: 16px;
`;
const QuizOption = styled(Button)`
  background: ${colors.coral};
  color: white;
  font-size: 1rem;
  margin: 0;
  border-radius: 18px;
  padding: 12px 0;
  box-shadow: none;
`;
const QuizResult = styled.div`
  margin-top: 32px;
  background: ${colors.cream};
  border-radius: 18px;
  padding: 24px 18px;
  color: ${colors.text};
  font-size: 1.1rem;
  box-shadow: 0 2px 8px rgba(255,90,95,0.06);
`;

const campaignIdeas = [
  {
    name: 'Plum Pals',
    desc: "Meet the animated dream team: Plum Pals! They turn insurance FAQs into LOLs and 'aha!' moments. Think Saturday morning cartoons, but for grown-up benefits."
  },
  {
    name: 'Care, Not Claims',
    desc: "Flip the script: a docu-series of real Plum moments where care came before claims. Heartfelt, binge-worthy, and guaranteed to make you believe in insurance again."
  },
  {
    name: "Plum's Day Out",
    desc: "A surprise office takeover! Pop-up Plum Care Corners with wellness games, instant checkups, and a 'Spin the Wheel of Good Fortune' for team treats."
  },
  {
    name: 'AI, But Make It Empathetic',
    desc: "Introducing Plum Buddy: the world's first AI that answers insurance questions with empathy, emojis, and the occasional dad joke. Because tech should feel like a hug."
  },
  {
    name: 'Open Claims, Open Hearts',
    desc: "A live, public dashboard of claims processed‚Äîplus thank-you notes, doodles, and even voice notes from real users. Radical transparency, radical trust."
  }
];

const quizQuestions = [
  {
    q: 'What excites you most about a campaign?',
    options: [
      'Fun, quirky storytelling',
      'Real impact on people',
      'Bringing people together',
      'Cool tech & AI',
      'Radical transparency'
    ]
  },
  {
    q: 'Pick a Plum value:',
    options: [
      'Human-first',
      'Empathy',
      'Community',
      'Innovation',
      'Openness'
    ]
  },
  {
    q: 'Your campaign superpower would be‚Ä¶',
    options: [
      'Turning boring into brilliant',
      'Making people feel seen',
      'Building hype and community',
      'Automating the magic',
      'Telling the truth, beautifully'
    ]
  },
  {
    q: "What's your go-to move when you join a new team?",
    options: [
      'Crack a joke to break the ice',
      "Listen and learn everyone's story",
      'Organize a team lunch or game',
      'Suggest a new tool or hack',
      'Share a wild, honest idea'
    ]
  },
  {
    q: "Just for fun: What's your sun sign?",
    options: [
      'Aries/Leo/Sagittarius',
      'Taurus/Virgo/Capricorn',
      'Gemini/Libra/Aquarius',
      'Cancer/Scorpio/Pisces',
      "I don't believe in sun signs!"
    ]
  }
];

function getMatchedCampaign(answers) {
  // Use the first 4 answers for matching
  if (answers.length < 5) return null;
  const counts = [0, 0, 0, 0, 0];
  answers.slice(0, 4).forEach(idx => { if (typeof idx === 'number') counts[idx]++; });
  const max = Math.max(...counts);
  const matchIdx = counts.indexOf(max);
  return campaignIdeas[matchIdx];
}

function CampaignMatchmaker() {
  const [step, setStep] = useState(0);
  const [answers, setAnswers] = useState([]);
  const matched = getMatchedCampaign(answers);
  const progress = Math.round((step / quizQuestions.length) * 100);

  return (
    <QuizSection>
      <QuizContainer>
        <SectionHeading style={{ margin: 0 }}>üéØ Campaign Matchmaker</SectionHeading>
        <div style={{ margin: '12px 0 24px 0' }}>
          <span style={{ color: colors.plum, fontWeight: 700 }}>Progress: {step}/{quizQuestions.length}</span>
          <div style={{ background: colors.accent, borderRadius: 8, height: 8, marginTop: 6 }}>
            <div style={{ width: `${progress}%`, height: 8, background: colors.coral, borderRadius: 8, transition: 'width 0.3s' }} />
          </div>
        </div>
        {step < quizQuestions.length ? (
          <>
            <QuizQ>{quizQuestions[step].q}</QuizQ>
            <QuizOptions>
              {quizQuestions[step].options.map((opt, i) => (
                <QuizOption
                  key={opt}
                  whileHover={{ scale: 1.04 }}
                  whileTap={{ scale: 0.97 }}
                  onClick={() => {
                    setAnswers([...answers, i]);
                    setStep(step + 1);
                  }}
                >
                  {opt}
                </QuizOption>
              ))}
            </QuizOptions>
          </>
        ) : (
          <QuizResult>
            <b>Your Plum-perfect campaign match:</b><br /><br />
            <span style={{ color: colors.plum, fontWeight: 700, fontSize: '1.2rem' }}>{matched.name}</span>
            <br /><br />
            {matched.desc}
            <br /><br />
            <Button
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              onClick={() => {
                setStep(0);
                setAnswers([]);
              }}
              style={{ marginTop: 12 }}
            >
              Try Again
            </Button>
          </QuizResult>
        )}
      </QuizContainer>
    </QuizSection>
  );
}

const BrainSection = styled.section`
  padding: 60px 0 30px 0;
  background: ${colors.cream};
`;

const BrainHeading = styled.h3`
  color: ${colors.plum};
  font-size: 2rem;
  font-weight: 900;
  margin: 2.5rem 0 1.2rem 0;
  text-align: center;
`;

const BrainDiagram = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 32px;
  margin: 0 auto;
  max-width: 700px;
`;

const BrainStep = styled.div`
  display: flex;
  align-items: center;
  background: ${colors.card};
  border-radius: 18px;
  box-shadow: 0 2px 8px rgba(255,90,95,0.06);
  padding: 18px 28px;
  font-size: 1.15rem;
  color: ${colors.text};
  min-width: 320px;
  max-width: 500px;
  position: relative;
`;

const BrainIcon = styled.span`
  font-size: 2rem;
  margin-right: 18px;
`;

const BrainArrow = styled.div`
  font-size: 2.2rem;
  color: ${colors.coral};
  margin: 0.5rem 0;
`;

// Add SVG doodles for each step (move to top level)
const doodles = [
  // Empathy
  () => (
    <svg width="40" height="40" viewBox="0 0 40 40" fill="none"><ellipse cx="20" cy="20" rx="18" ry="10" stroke="#FF5A5F" strokeWidth="2"/><ellipse cx="20" cy="20" rx="6" ry="3" fill="#FDE68A"/></svg>
  ),
  // Brainstorm
  () => (
    <svg width="40" height="40" viewBox="0 0 40 40" fill="none"><path d="M10 30 Q20 10 30 30" stroke="#6C2676" strokeWidth="2" fill="none"/><circle cx="20" cy="20" r="4" fill="#FF5A5F"/></svg>
  ),
  // Aha
  () => (
    <svg width="40" height="40" viewBox="0 0 40 40" fill="none"><circle cx="20" cy="20" r="10" stroke="#FDE68A" strokeWidth="2"/><path d="M20 10 v-6" stroke="#FF5A5F" strokeWidth="2"/><path d="M20 36 v-6" stroke="#FF5A5F" strokeWidth="2"/></svg>
  ),
  // Tech
  () => (
    <svg width="40" height="40" viewBox="0 0 40 40" fill="none"><rect x="10" y="15" width="20" height="10" rx="3" stroke="#6C2676" strokeWidth="2"/><circle cx="20" cy="20" r="2" fill="#FF5A5F"/></svg>
  ),
  // Story
  () => (
    <svg width="40" height="40" viewBox="0 0 40 40" fill="none"><rect x="12" y="12" width="16" height="16" rx="4" stroke="#FF5A5F" strokeWidth="2"/><path d="M16 20 h8" stroke="#6C2676" strokeWidth="2"/></svg>
  ),
  // Test
  () => (
    <svg width="40" height="40" viewBox="0 0 40 40" fill="none"><ellipse cx="20" cy="28" rx="10" ry="4" stroke="#FDE68A" strokeWidth="2"/><circle cx="20" cy="20" r="4" fill="#6C2676"/></svg>
